export const AlertState = ({children})=>{
const [state, dispatch] = useReducer(alertReducer, {visible: false})

const show = (text, type = 'warning') =>{
    dispatch({
        type: SHOW_ALERT,
        payload: {text, type}
    })
}
const hide =()=> dispatch({type: HIDE_ALERT})

    return(
        <AlertContext.Provider>
            {children}
        </AlertContext.Provider>
    )
}
