export const Form = () => {
    const [value, setValue] = useState('')
 const submitHandler = event =>{
     event.preventDefault()

 }
    return (
        <form onSubmit={submitHandler}>
            <div className="form-group">
                <input
                    type="text"
                    placeholder="Введите название заметки"
                    className="form-control"
